	function sortTheBars(a,b,sortByAscending){
		var aClose=a.close;
		var bClose=b.close;
		if(sortByAscending){
			if(!isNaN(a.close) && a.close!=null){
				if(a.close>b.close){
					return 1;
				}
				else if(a.close<b.close){
					return -1;
				}
				else{
					return 0;
				}
			}
			else{
				return 0;
			}
		}
		else{
			if(!isNaN(a.close) && a.close!=null){
				if(a.close>b.close){
					return -1;
				}
				else if(a.close<b.close){
					return 1;
				}
				else{
					return 0;
				}
			}
			else{
				return 0;
			}
		}
	
	}
